import 'package:flutter/material.dart';
import '../../screens/musicscreen.dart';

class bollywood extends StatefulWidget {
  @override
  _bollywoodState createState() => _bollywoodState();
}

class _bollywoodState extends State<bollywood> {
  List s = [
    {
      "songpic": "data:image/jpeg;base64,",
      "songname": "war",
    },
    {
      "songpic": "data:image/jpeg;base64,",
      "songname": "acharya",
    },
    {
      "songpic": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRe3wtdJxnkprzF3fLP8Jki5VSR_P8BIkxHMQ&usqp=CAU",
      "songname": "pushpa",
    },
    {
      "songpic": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSOAndFnANIj5Me9WXraKH5ZkkljSqFRgg1GA&usqp=CAU",
      "songname": "",
    },
  ];
  @override
  Widget build(BuildContext context) {
    return Container(
        margin: EdgeInsets.only(top: 30, left: 10, right: 10),
        child: Column(crossAxisAlignment: CrossAxisAlignment.start, children: [
          gethead(),
          getlist(),
        ]));
  }

  Widget getlist() {
    return Container(
        margin: EdgeInsets.symmetric(vertical: 10.0),
        height: 120.0,
        child: ListView.builder(
            scrollDirection: Axis.horizontal,
            itemCount: s.length,
            itemBuilder: (BuildContext context, int index) {
              return GestureDetector(
                  onTap: () {
                    Navigator.push(
                      context,
                      MaterialPageRoute(
                          builder: (context) => music(
                                songurl: s[index]["songpic"],
                              )),
                    );
                  },
                  child: Container(
                      width: 120,
                      margin: EdgeInsets.all(4.0),
                      decoration: BoxDecoration(color: Colors.grey, borderRadius: BorderRadius.circular(5.0)),
                      child: Image(
                        fit: BoxFit.cover,
                        image: NetworkImage(s[index]["songpic"]),
                      )));
            }));
  }

  Widget gethead() {
    return Container(
      child: Text("Your Playlists",
          style: TextStyle(
            color: Colors.white,
            fontWeight: FontWeight.bold,
            fontSize: 20.0,
          )),
    );
  }
}
